CREATE TRIGGER `close_old_comments` BEFORE UPDATE ON `posts` FOR EACH ROW BEGIN
    IF TIMESTAMPDIFF(MONTH, OLD.date_created, NEW.date_updated) >= 1 THEN
        UPDATE comments SET is_public = FALSE WHERE post_id = OLD.id;
    END IF;
END
$$
